  if (navigator.platform.indexOf('Win') != -1)
    mw.loader.load('//en.wikipedia.org/w/index.php?title=MediaWiki:Common.css/WinFixes.css&action=raw&ctype=text/css')

   switch( mw.config.get('wgNamespaceNumber') ){
    case 0: case 100:
      $(LinkFA)
      importMW('Osm')
      importMW('Collapserefs')
      if( wgArticleId==4401 ) importMW('Mainpage')
      break
    case 6:
      importMW('Filepage')
      break
   }    

}


